import { ACTIONS } from 'redux/actions/types.js'
import { mainAction } from "redux/actions/index.actions"

const initialState = {
    headers:[
        {
          "id": "1",
          "name": "home",
          "header": "home_search_header"
        },
        {
          "id": "2",
          "name": "agenda",
          "header": "agenda_search_header"
        },
        {
          "id": "3",
