import React,{useState} from 'react'
import { Form,  Row, Col, Button, Table } from "react-bootstrap";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { ACTIONS } from "redux/actions/types";
import { mainAction } from "redux/actions/index.actions"
const PresentationMusicUpload = () => {
    const [file,setFile] = useState(null)


    const onFormSubmit = (e) =>{
        e.preventDefault() // Stop form submit

        fileUpload(file).then((response)=>{
            console.log(response.data);
        })
    }
    const onChange = (e) => {
        setFile({file:e.target.files[0]})
    }
    const fileUpload =(file)=>{
        const url = 'http://example.com/file-upload';
        const formData = new FormData();
        formData.append('file',file)
        const config = {
            headers: { 'content-type': 'multipart/form-data'}
        }
        return  //post(url, formData,config)
    }
    const renderMusicTracks = () => {
        const tracks = props.profile.music ? props.profile.music : [] 

       return  Object.values(tracks).map(track=>{
            return (<>
                <td>
             <figcaption>{`Listen to ${track.musicName}`}</figcaption>
             <audio
                 controls
                 src={`https://firebasestorage.googleapis.com/v0/b/johandemeij-513b2.appspot.com/o/music%2F${track.musicName}?alt=media`}>
                     Your browser does not support the
                     <code>audio</code> element.
             </audio>
         </td>
         <td><Button type="submit">Delete</Button></td>
                </>)
        })

    }
    return (
        <section id="music">
            <Form onSubmit={()=>onFormSubmit()}>
                <Row>
                    <Col>
                        <h1>File Upload</h1>
                        <input type="file" onChange={(e)=>onChange(e)} />
                    </Col>
                    <Col>
                        <Button type="submit">Add music</Button>
                    </Col>
                </Row>
            </Form>
            <Row style={{marginTop:"15px"}}>
                <Col lg={3}>
                <Table striped bordered hover>
  <thead>
    <tr>
      <th colSpan="2">music</th>

    </tr>
  </thead>
  <tbody>
    <tr>
      {renderMusicTracks()}
    </tr>

  </tbody>
</Table>
                </Col>
            </Row>
        </section>
   )
}


function mapStateToProps(state) {

    return {
      music:state.presentationMusicReducer
    };
  }

function mapDispatchToProps(dispatch) {
    return {
      actions: bindActionCreators({mainAction}, dispatch)
    };
  }

export default connect(mapStateToProps, mapDispatchToProps)(PresentationMusicUpload);
