import React,{useEffect} from 'react'
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { mainAction } from 'redux/actions/index.actions'
import { Button } from "react-bootstrap"
import { ACTIONS } from "redux/actions/types"
import { Link } from "react-router-dom";
const Posts = (props) => {
    useEffect(() => {
        // Update the document title using the browser API
        document.title = `JDM Dashboard | posts`;
        //if(props.categories.length === 0)
       props.actions.mainAction(ACTIONS.LOAD_POSTS)
    },[])
    console.log(props)
    const PostList = () => {
        return Object.values(props.posts).map(post =>{
            console.log(post)
          return ( <tr>
            <td className="post-title"><Link to="/dashboard/edit">{post.postTitle}</Link></td>
            <td className="post-date">{post.postDate}</td>
            <td className="post-actions"><Button className="btn btn-success">Preview</Button> <Button className="btn btn-danger">Delete</Button></td>
          </tr> )
        })
    }
    return (<>
    <table className="table table-hover">
  <thead className="thead-dark">
    <tr>
      <th scope="col">Title</th>
      <th scope="col">Date</th>
      <th scope="col"></th>
    </tr>
  </thead>
  <tbody>
   <PostList />
  </tbody>
</table>
    </>)
}

function mapStateToProps(state) {
    return {
      posts:state.postsReducer
    };
  }

  function mapDispatchToProps(dispatch) {
    return {
      actions: bindActionCreators(
        {
            mainAction
        },
        dispatch
      )
    };
  }

  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(Posts);
