import React from "react";
import Pagination from 'react-bootstrap/Pagination'
import {Row,Col} from "react-bootstrap"
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { mainAction } from 'redux/actions/index.actions'
import { ACTIONS } from "redux/actions/types"

const Paginate = (props) => {
    const loadAgendas = (skip) => {
        props.actions.mainAction(ACTIONS.LOAD_AGENDAS,skip) 
    }

    const renderPageItems = () => {
        let totalPages = props.agendas ? props.agendas.length:""
        let pages = Math.ceil(totalPages / 11);
        console.log(pages)
        const Items = []
        for (let i = 1; i < 11; i++) {

            let pagesToSkip = i === 1 ? 0: Math.ceil(i * pages)
            console.log(pagesToSkip)
            Items.push(<Pagination.Item  onClick={()=>loadAgendas(pagesToSkip)}>{i}</Pagination.Item> )
        }
        return Items
    }
    console.log(props)
    return(<Row>
        <Col lg={{span:6,offset:3}}>
        <Pagination>
  <Pagination.First/>
  <Pagination.Prev />

  {renderPageItems()}


  {/* {<Pagination.Item disabled>{14}</Pagination.Item>} */}

  {/* {<Pagination.Ellipsis />} */}
  <Pagination.Next />
  <Pagination.Last />
</Pagination>
        </Col>
        </Row>)
}
function mapStateToProps(state) {
    return {
        agendas:state.agendaReducer
    };
  }

  function mapDispatchToProps(dispatch) {
    return {
      actions: bindActionCreators(
        {
            mainAction
        },
        dispatch
      )
    };
  }

  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(Paginate);
