import React,{ useEffect } from "react";
import {Row,Col,Container} from "react-bootstrap"
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { mainAction } from 'redux/actions/index.actions'
import { ACTIONS } from "redux/actions/types"
import ReactHtmlParser from 'react-html-parser';
const Music  = (props) => {
    useEffect(() => {
        window.scrollTo(0,0)
        document.title = "JohanDeMeij.com | Music"
       props.actions.mainAction(ACTIONS.LOAD_MUSIC,{})
       // eslint-disable-next-line react-hooks/exhaustive-deps
    },[])
    console.log(props)
    return (<>
    <section className="music">

    <Container>
         <Row>
         <Col>
             <h3 style={{fontSize:"1.5rem",textTransform: "uppercase"}}>Music</h3>
            <ul className="categories">
                {props.categories.map(category => {
                    return <li><a href="">{ReactHtmlParser(category)}</a></li>
                })}
            </ul>
        </Col>
        </Row>
        <Row>
            <Col lg={{span:6}}>
            <ul>
                {props.leftColumn.map((presentation ,i)=>{
                    if(i < 77)
                    return <li>{i}<a href="">{ReactHtmlParser(presentation.cdName)}</a></li>
                })}
            </ul>
            </Col>
            <Col lg={{span:6}}>
            <ul>
                {props.leftColumn.map(presentation=>{
                    if(i > 77)
                    return <li><a href="">{ReactHtmlParser(presentation.cdName)}</a></li>
                })}
            </ul>
            </Col>
        </Row>
    </Container>
    </section>
    </>)
}
function mapStateToProps(state) {
    return {
        categories:state.musicReducer.categories,
        leftColumn:state.musicReducer.leftColumn,
        rightColumn:state.musicReducer.rightColumn,
    };
  }

  function mapDispatchToProps(dispatch) {
    return {
      actions: bindActionCreators(
        {
            mainAction
        },
        dispatch
      )
    };
  }

  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(Music);
