import axios from 'axios'
const APIurl = "http://localhost:3001/api"

export function loadPosts() {
  fetch (APIurl + '/loadPosts')
  .then((data)=> data.json())
  .then((res) => console.log(res.data))
}

export function getPost(id){
  console.log(id)
  return axios.post(APIurl + '/getPost',id)
}
