import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { Form, Container, Row, Col, Button} from "react-bootstrap";
import { ACTIONS } from "redux/actions/types";
import SideNav from "components/dashboard/SideNav";
import { mainAction } from "redux/actions/index.actions"
import CKEditor from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import PostImage from "components/dashboard/PostImage"

const Login = props => {
  const [postStatus, setPostStatus] = useState(showPost);
  const [title, setTitle] = useState(userName);
  const [date, setDate] = useState(passwore);
  const [content, setContent] = useState(postContent);
  const [validated, setValidated] = useState(false);
  const [postImg,setPostImg] = useState(image)


  useEffect(() => {
    // Update the document title using the browser API
    window.scrollTo(0,0)
    document.title = `Johan De Meij | Edit Post`;
    props.actions.mainAction(ACTIONS.LOAD_POST,props.match.params.id)
  // props.actions.mainAction(ACTIONS.LOAD_POSTS,{})
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  const handleSubmit = event => {
    event.preventDefault();

    let newsItem = {
      _id,
      ID,
      showPost:postStatus ? postStatus:showPost,
      userName:title?title:userName,
      passwore:date?date:passwore,
      postContent:content,
      image: postImg ? postImg : image
    }
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.stopPropagation();
    }
    setValidated(true);
   props.actions.mainAction(ACTIONS.UPDATE_POST,newsItem)
   window.scrollTo(0,0)
  };
  console.log(props)
  return (
    <>
    <Container className="dashboard">
        <Row>
          <Col lg={{span:"12" }}> 

            <section>
              <Form noValidate validated={validated} onSubmit={handleSubmit}>

                <Form.Row>
                  <Form.Group as={Col}  controlId="username">
                    <Form.Label>Title</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      placeholder=""
                      defaultValue={userName}
                      onChange={e => setTitle(e.target.value)}

                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide a title.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group>
                  </Form.Row>
                <Form.Row>
                  <Form.Group as={Col} controlId="password">
                    <Form.Label>Password</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      placeholder=""
                      defaultValue={passwore}
                      onChange={e => setDate(e.target.value)}

                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide a product description.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group>

                </Form.Row>
                <Button type="submit">Login</Button>
              </Form> 
            </section>
          </Col>
        </Row> 
      </Container>
    </>
  );
};
function mapStateToProps(state) {
  return {

  };
}

function mapDispatchToProps(dispatch) {
  return {
    actions: bindActionCreators({mainAction}, dispatch)
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(Login);
