import { ACTIONS } from 'redux/actions/types.js'
import { mainAction } from "redux/actions/index.actions"
import { db } from "../../firebase";
import _ from 'lodash'
const initialState = {
};
export default function agendaReducer (state = initialState, action) {
  console.log(action)
    switch (action.type) {
      case ACTIONS.CREATE_NEW_AGENDA:{
        let stateCopy = _.cloneDeep(action.payload)
        db.collection("agendas")
        .add(stateCopy).then((docRef)=>{
          db.collection("agendas")
          .doc(docRef.id)
          .update(stateCopy).then(()=>{
            stateCopy._id = docRef.id
            action.asyncDispatch(mainAction(ACTIONS.CREATE_NEW_AGENDA_SUCCESS,stateCopy))
          });
        })
      return stateCopy
      }
      case ACTIONS.CREATE_NEW_AGENDA_SUCCESS:{
        return action.payload
      }
      case ACTIONS.DELETE_AGENDA:{

        db.collection("agendas")
        .where('id','==',action.payload)
         .get()
         .then((querySnapshot) => {
           querySnapshot.forEach((doc)=> doc.ref.delete())
           action.asyncDispatch(mainAction(ACTIONS.DELETE_AGENDA_SUCCESS,[]))
         });
       return state
     }  
     case ACTIONS.DELETE_AGENDA_SUCCESS:{
       action.asyncDispatch(mainAction(ACTIONS.LOAD_AGENDAS,[]))
       return state
     }  
      case ACTIONS.LOAD_CD_AGENDA: {
      let d = new Date();
      let year = d.getFullYear().toString();

       let stateCopy = []
       db.collection("agendas")
     .where("year",'==',year)
   .orderBy("month","desc")
    .orderBy("day","asc")
      .get()
      .then(querySnapshot => {
        const data = querySnapshot.docs.map(doc => {

          doc.data()});
        data.map((newData,i)=>{
          console.log(newData)
          if(i < action.payload.limit && i > action.payload.skip)
          stateCopy.push(newData)
        })
        action.asyncDispatch(mainAction(ACTIONS.LOAD_CD_AGENDA_SUCCESS,stateCopy))
      });
        return state
      }
      case ACTIONS.LOAD_CD_AGENDA_SUCCESS: {
        return action.payload
      }
      case ACTIONS.LOAD_CD_AGENDA_FAIL: {

        return state
      }
      case ACTIONS.LOAD_AGENDAS: {
        let d = new Date();
        let day = d.getDay();
        let month = (d.getMonth() + 1).toString()
        let year = d.getFullYear().toString();

         let stateCopy = ''
         //=======================================================
         db.collection("agendas")
       //.where("year",'==',year)
      .where("month",'>=',month)
      .orderBy("month","asc")
      .orderBy("day","asc")
        .get()
        .then(querySnapshot => {
          const data = querySnapshot.docs.map(doc => doc.data());
          stateCopy = data;

          //=======================================================
            db.collection("presentations")
           .get()
            .then(querySnapshot => {
              const data = querySnapshot.docs.map(doc => doc.data());
              stateCopy.map((post,i)=>{
              let title = data.filter(presentation=>presentation.id===post.cd).map(presentation=>{return presentation.cdName})
              post.title = title[0]; 
              post.date=`${post.year}-${post.month}-${post.day}`
              return post
              })
              action.asyncDispatch(mainAction(ACTIONS.LOAD_AGENDAS_SUCCESS,stateCopy))
            });
          //========================================================
        });



        return state
      }
      case ACTIONS.LOAD_AGENDAS_SUCCESS:{
        let stateCopy = _.cloneDeep(action.payload)
        let d = new Date();
        let year = d.getFullYear().toString();


        return stateCopy.filter(agendas=>agendas.year === year)
      }
      case ACTIONS.LOAD_AGENDAS_FAIL:{
        return state
      }
      case ACTIONS.LOAD_AGENDA:{
      let _id,stateCopy 
      db.collection("agendas")
      .where("id","==",action.payload)
      .get()
      .then(querySnapshot => {
        const data = querySnapshot.docs.map(doc => {
          _id = doc.id
          return doc.data()});
          stateCopy= data
          stateCopy[0]._id = _id


        if(data.length > 0){
        action.asyncDispatch(mainAction(ACTIONS.LOAD_AGENDA_SUCCESS,stateCopy))
        } else {
          action.asyncDispatch(mainAction(ACTIONS.LOAD_AGENDA_FAIL,{error:"Failed to retrieve agenda"}))
        }
      });
      return state
      }
      case ACTIONS.LOAD_AGENDA_SUCCESS:{
        return {...action.payload[0]}
      }
      case ACTIONS.LOAD_AGENDA_FAIL:{
        return state
      }
      case ACTIONS.UPDATE_AGENDA:{
        console.log(action)
          db.collection("agendas")
          .doc(action.payload._id)
          .update(action.payload).then(()=>{
            action.asyncDispatch(mainAction(ACTIONS.UPDATE_AGENDA_SUCCESS,action.payload))
          });
          return state
          }
          case ACTIONS.UPDATE_AGENDA_SUCCESS:{
          let stateCopy = _.cloneDeep(state)
          action.asyncDispatch(mainAction(ACTIONS.LOAD_AGENDA,stateCopy.id))
          return {state,...action.payload}
          }
      default: 
        return {
          ...state
        }
    }
  }
