import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { Form, Container, Row, Col, Button} from "react-bootstrap";
import { ACTIONS } from "redux/actions/types";
import SideNav from "components/dashboard/SideNav";
import { mainAction } from "redux/actions/index.actions"
import dayjs from "dayjs"
import _ from "lodash"

const NewOrderLocation = props => {
    let d = new Date()
    const [id, setId] = useState(dayjs(d).format('YYYYMMDDHHmmss'));
    const [_id, set_id] = useState("");
    const [address, setAddress] = useState();
    const [city, setCity] = useState("");
    const [company, setCompany] = useState("");
    const [country, setCountry] = useState("");
    const [email, setEmail] = useState("");
    const [fax, setFax] = useState("");
    const [state, setState] = useState("");
    const [zip, setZip] = useState("");
    const [subCountry, setSubCountry] = useState("");
    const [tel, setTel] = useState("");
    const [website, setWebsite] = useState("");
    const [validated, setValidated] = useState();


  useEffect(() => {
    // Update the document subCountry using the browser API
    window.scrollTo(0,0)
    document.subCountry = `Johan De Meij | Edit Post`;
    props.actions.mainAction(ACTIONS.LOAD_ORDER_LOCATIONS,[])
    if(props.match.params.id)
    props.actions.mainAction(ACTIONS.LOAD_ORDER_LOCATION,props.match.params.id)
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  const handleSubmit = event => {
    event.preventDefault();

    let catalogueOrder = {
        id,
        _id,
        address,
        city,
        company,
        country,
        email,
        fax,
        state,
        zip,
        subCountry

    }
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.stopPropagation();
    }
    setValidated(true);
    console.log(catalogueOrder)
   props.actions.mainAction(ACTIONS.CREATE_CATALOGUE,catalogueOrder)
   props.history.push('/dashboard/catalogue/'+props.lastItem)
  };

  const handleUpdate = event => {
    event.preventDefault();

    let catalogueOrder = {
        id:props.catalogue.id,
        address : document.getElementById("address").value,
        city : document.getElementById("city").value,
        company : document.getElementById("company").value,
        country :document.getElementById("country").value,
        email ,
        fax : document.getElementById("fax").value,
        state : document.getElementById("state").value,
        subCountry : document.getElementById("subCountry").value

    }
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.stopPropagation();
    }
    setValidated(true);
   props.actions.mainAction(ACTIONS.UPDATE_CATALOGUE,catalogueOrder)
   props.history.push('/dashboard/catalogue/'+id)
