import React, { useState, useEffect, useCallback } from "react";
import { connect } from "react-redux";
import { Button } from "react-bootstrap";
import { bindActionCreators } from "redux";
import { ACTIONS } from "redux/actions/types";
import { mainAction } from "redux/actions/index.actions";
import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader
import { Carousel } from 'react-responsive-carousel';
import LazyLoad from 'react-lazyload';
import 'react-alice-carousel/lib/alice-carousel.css'
const PhotoGallery = (props) => {
  const [ currentIndex, setCurrentIndex ] = useState(0)
  const [ items, setItems ] = useState([1,2,3,4,5])
  const slideTo = (i) => setCurrentIndex({ currentIndex: i });
  const slideNext = () => setCurrentIndex({ currentIndex: currentIndex + 1});
  const slidePrev = () => setCurrentIndex({ currentIndex: currentIndex - 1 });
  const [slideLocation,setSlideLocation] = useState(0) 
  const [onSlideChanged,setSlideChanged] = useState() 
  useEffect(() => {
    document.title = "JohanDeMeij.com | Home";
    props.actions.mainAction(ACTIONS.LOAD_GALLERY,[])
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const renderImages = () => {
   return Object.values(props.photos).map(photo=>{
      return ( <li className="slide"><LazyLoad width="100%" height="100%" object-fit="cover" ><img src={`https://firebasestorage.googleapis.com/v0/b/johandemeij-513b2.appspot.com/o/gallery%2F${photo.name}?alt=media`} width="100%" height="100%" object-fit="cover"/>
      {photo.caption ? <p className="legend">{photo.caption}</p>:""}</LazyLoad></li>)
    })
  }
const slideImage = () =>{
  return setSlideLocation("-100%")
}
  const renderThumbnails = () => {
    return Object.values(props.photos).map((photo,i)=>{
      return ( <li onClick={()=>slideImage()} className="thumb" ariaLabel={"slide item "+i} role="button" tabindex="0" style={{width: "80px"}}><img src={`https://firebasestorage.googleapis.com/v0/b/johandemeij-513b2.appspot.com/o/gallery%2F${photo.name}?alt=media`} width="100%" height="100%" object-fit="cover"/>
      </li>)
    })
  }


  const handleOnDragStart = (e) => e.preventDefault()
  return (<>
  <section className="photo-gallery">
    <div className="container">
      <div className="row">
      <div className="col-10 offset-1 text-center">
        <h1 style={{color: "rgb(0, 0, 0)", textAlign:"center", marginBottom: "25px"}}>Gallery</h1>
{/* {  <LazyLoad width="100%" height="100%" object-fit="cover" >
    <Carousel autoPlay interval="5000" transitionTime="1000" showThumbs={false}  showIndicators={false} infiniteLoop={true} >
      {renderImages()}

    </Carousel>
    </LazyLoad>} */}
    <div className="galleryContainer">
      <ul style={{transform: `translate3d(${slideLocation}, 0px, 0px)`, transitionDuration:" 350ms"}}>{renderImages()}</ul>
    </div>

    <ul>{renderThumbnails()}</ul>
      </div>
      </div>
    </div>
  </section>
  </>)
}

function mapStateToProps(state) {
  return {
    photos:state.galleryReducer.gallery
  };
}

function mapDispatchToProps(dispatch) {
  return {
    actions: bindActionCreators(
      {
        mainAction,
      },
      dispatch
    ),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(PhotoGallery);
