import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { Form, Container, Row, Col, Button, InputGroup} from "react-bootstrap";
import { ACTIONS } from "redux/actions/types";
import Navbar from "components/dashboard/Navbar";
import SideNav from "components/dashboard/SideNav";
import { mainAction } from "redux/actions/index.actions"
const EditPost = props => {
  const {postTitle,postDate,postContent} = props.post
  const [showPost, setShowPost] = useState(false);
  const [title, setTitle] = useState(postTitle);
  const [date, setDate] = useState();
  const [content, setContent] = useState();
  const [postImage, setPostImage] = useState();
  const [postImageCaption, setPostImageCaption] = useState();
  const [validated, setValidated] = useState(false);
    const [description, setDescription] = useState();
    const [price, setPrice] = useState();
    const [type, setType] = useState();
  useEffect(() => {
    // Update the document title using the browser API
    document.title = `Johan De Meij | Edit Post`;

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  const handleSubmit = event => {
    event.preventDefault();
    let product = {
      title,
      description,
      price,
      productType:type,
      productOwnerId: props.user._id
    }
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.stopPropagation();
    }
    setValidated(true);
    console.log(ACTIONS.ADD_PRODUCT,product)
    if(Object.values(product).length > 0)
    props.actions.mainAction(ACTIONS.ADD_PRODUCT,product)
  };
  return (
    <>
      <Container>
    <Row>
        <Col lg={{span:"4" ,offset:"4"}}>
          <section id="product">
        <Form noValidate validated={validated} onSubmit={handleSubmit}>
      <Form.Row>
        <Form.Group as={Col}  controlId="title">
          <Form.Label>Product Name</Form.Label>
          <Form.Control
            required
            type="text"
            placeholder=""
            defaultValue={title}
            onChange={e => setTitle(e.target.value)}

          />
          <Form.Control.Feedback type="invalid">
              Please provide a title.
            </Form.Control.Feedback>
          <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
        </Form.Group>
        </Form.Row>
        <Form.Row>
        <Form.Group as={Col} controlId="description">
          <Form.Label>description</Form.Label>
          <Form.Control
            required
            type="text"
            placeholder=""
            defaultValue={description}
            onChange={e => setDescription(e.target.value)}

          />
          <Form.Control.Feedback type="invalid">
              Please provide a product description.
            </Form.Control.Feedback>
          <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
        </Form.Group>

      </Form.Row>
      <Form.Row>
      <Form.Group as={Col} controlId="price">
          <Form.Label>price</Form.Label>
          <InputGroup>
            <Form.Control
            required
            type="text"
            placeholder=""
            defaultValue={price}
            onChange={e => setPrice(e.target.value)}

          />
            <Form.Control.Feedback type="invalid">
              Please enter a price.
            </Form.Control.Feedback>
          </InputGroup>
        </Form.Group>
        </Form.Row>
        <Form.Row>
      <Form.Group as={Col} controlId="type">
          <Form.Label>type</Form.Label>
          <Form.Control
            required
            type="text"
            placeholder=""
            defaultValue={type}
            onChange={e => setType(e.target.value)}

          />
          <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
        </Form.Group>

      </Form.Row>
      <Button type="submit">Add Product</Button>
    </Form> </section>
        </Col>
      </Row> 
    </Container>
    </>
  );
};
function mapStateToProps(state) {
  return {
    post: state.singlePostReducer
  };
}

function mapDispatchToProps(dispatch) {
  return {
    actions: bindActionCreators({mainAction}, dispatch)
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(EditPost);
