import { ACTIONS } from 'redux/actions/types.js'
import { loadPostImages } from "API/indexAPI"
import { mainAction } from "redux/actions/index.actions"

const initialState = {};
export default function postImagesReducer (state = initialState, action) {
    switch (action.type) {

      case ACTIONS.LOAD_POST_IMAGES:{
       fetch('http://localhost:3001/api/loadPostImages')
       .then(json=>{
        console.log(json)
       })
       .catch(err=>{

       })
        return {...action.payload}
      }
      case ACTIONS.LOAD_POST_IMAGES_SUCCESS:{
        return {state,...action.payload}
      }
      case ACTIONS.LOAD_POST_IMAGES_FAIL:{
        return state
      }
      default: 
        return state

    }
  }
