import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { mainAction } from "redux/actions/index.actions"
import { Table, Container, Row, Col, Button} from "react-bootstrap";
import { ACTIONS } from "redux/actions/types";

const AdditionalInfo = (props) => {
    useEffect(() => {
        // Update the document title using the browser API
        window.scrollTo(0,0)
        props.actions.mainAction(ACTIONS.LOAD_CD_INFO,props.cdId)
        // eslint-disable-next-line react-hooks/exhaustive-deps
      }, []);
      const List = () => {
          const ListItems = () =>{
              return Object.values(props.additionalInfo).map(info => {
              return <tr><td>{info.title[0]}</td><td></td><td></td></tr>
              })
          }
          return (
            <Table striped bordered hover>
            <thead>
              <tr>
                <th>#</th>
                <th>First Name</th>
                <th>Last Name</th>
                <th>Username</th>
              </tr>
            </thead>
            <tbody>
              <ListItems />
            </tbody>
          </Table>
          )
      }
      console.log(props)
      return (<>
        <List />
      </>)
}

function mapStateToProps(state) {

    return {
      additionalInfo:state.cdInfoReducer
    };
  }

  function mapDispatchToProps(dispatch) {
    return {
      actions: bindActionCreators({mainAction}, dispatch)
    };
  }

  export default connect(mapStateToProps, mapDispatchToProps)(AdditionalInfo);
