import React,{useEffect} from "react"
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { mainAction } from 'redux/actions/index.actions'
import { ACTIONS } from "redux/actions/types"
import { Container } from "react-bootstrap";
const Cds = (props) => {
    useEffect(() => {
        window.scrollTo(0,0)

       props.actions.mainAction(ACTIONS. LOAD_CDS,{})
       // eslint-disable-next-line react-hooks/exhaustive-deps
    },[])
    return (<>
    <ul>
    {Object.values(props.cds).map((cd)=>{
        return <li key={cd._id}> <div>
        <img src={`/images/music_catalogue/${cd.cdImage}`} style={{width:"100%",backgrounSize:"cover",backgroundPosition:"50% 10%",marginBottom:"25px"}} alt="biogrophy"/>
    </div>{cd.cd_name}</li>
    })}
    </ul>
    </>)
}
function mapStateToProps(state) {
    return {
        cds:state.cdsReducer
    };
  }

  function mapDispatchToProps(dispatch) {
    return {
      actions: bindActionCreators(
        {
            mainAction
        },
        dispatch
      )
    };
  }

  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(Cds);
