import React,{useEffect,useState} from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { mainAction } from 'redux/actions/index.actions'
import { ACTIONS } from "redux/actions/types"
import {Row,Container,Col} from "react-bootstrap"
import ReactHtmlParser from 'react-html-parser';
const MusicProfile= (props)=>{
    useEffect(() => {
        window.scrollTo(0,0)
        document.title = "JohanDeMeij.com | Music Profile"
        props.actions.mainAction(ACTIONS.LOAD_MUSIC_PROFILE,props.match.params.id)
       // eslint-disable-next-line react-hooks/exhaustive-deps
    },[])
const CleanUpSynopsis = (text)  => {
    return text.replace(/(\r\n|\n|\r)/gm, "")
}
const renderProfile = () => {
    return(<>
        <section className="music-profile">
           <Container>
               <Row>
                   <Col lg={{span:4}}>
        <Row><div style={{backgroundImage:`url(/images/posts/${props.profile.frontCover !== null ? props.profile.frontCover:""})`,backgroundSize:"cover",width:"100%",minHeight:"290px"}}></div>
        <div  style={{fontSize:".9rem",margin:"15px 0"}}>{props.profile.frontCaption}</div></Row>
                       <Row><div style={{backgroundImage:`url(/images/posts/${props.profile.backCover})`,backgroundSize:"cover",width:"100%",minHeight:"290px",margin:"0 0 15px"}}></div>
                       <div style={{fontSize:".9rem",margin:"15px 0"}}>{props.profile.backCaption}</div>
                       </Row>
                   </Col>
                   <Col lg={{span:8}}>
        <h1>{props.profile.cdName}</h1>
        <h3>{props.profile.subTitle}</h3>
        <h5>{`Composer: ${props.profile.subTitle}`}</h5>
        <p>{ReactHtmlParser(props.profile.synopsis ? CleanUpSynopsis(props.profile.synopsis):"props.profile.synopsis")}</p>
                   </Col>
               </Row>
           </Container>
        </section>
        </>)
}

 return(<>
{typeof props.profile.frontCover === 'string' ? renderProfile():"no profile found"}
 </>)
}
function mapStateToProps(state) {
    return {
        profile:state.musicProfileReducer
    };
  }

  function mapDispatchToProps(dispatch) {
    return {
      actions: bindActionCreators(
        {
            mainAction
        },
        dispatch
      )
    };
  }

  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(MusicProfile);
