import { mainAction } from "redux/actions/index.actions"
import { auth} from "../../firebase";
const initialState = {

};
export default function userReducer (state = initialState, action) {
    switch (action.type) {

      case ACTIONS.LOGIN_USER: {
        auth.signInWithEmailAndPassword(action.payload.email, action.payload.password).catch(error => {
            action.asyncDispatch(mainAction(ACTIONS.LOGIN_USER_FAIL,{message:"Error signing in with password and email",error}))
          });
        return state
      }
      case  ACTIONS.LOGIN_USER_SUCCESS: {

        return state
      }
      case  ACTIONS.LOGIN_USER_FAIL: {

        return state
      }

      default: 
        return {
          ...state
        }
    }
  }
