import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { Form, Container, Row, Col, Button} from "react-bootstrap";
import { ACTIONS } from "redux/actions/types";
import SideNav from "components/dashboard/SideNav";
import { mainAction } from "redux/actions/index.actions"
import CKEditor from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import _ from "lodash"

const NewMusicCatalogue = props => {
    const [id, setId] = useState(false);
    const [_id, set_id] = useState(false);
    const [catalogueNumber, setCatalogueNumber] = useState(false);
    const [composerArranger, setComposerArranger] = useState(false);
    const [duration, setDuration] = useState(false);
    const [instrumentation, setInstrumentation] = useState(false);
    const [link, setLink] = useState(false);
    const [priceInDollars, setPriceInDollars] = useState(false);
    const [priceInEuros, setPriceInEuros] = useState(false);
    const [title, setTitle] = useState(false);
    const [validated, setValidated] = useState(false);



  useEffect(() => {
    // Update the document title using the browser API
    window.scrollTo(0,0)
    document.title = `Johan De Meij | Edit Post`;
   // props.actions.mainAction(ACTIONS.LOAD_DASHBOARD_POSTS,{})
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  const handleSubmit = event => {
    event.preventDefault();

    let catalogueOrder = {
        id,
        _id,
        catalogueNumber,
        composerArranger,
        duration,
        instrumentation,
        link,
        priceInDollars,
        priceInEuros,
        title

    }
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.stopPropagation();
    }
    setValidated(true);
    console.log(catalogueOrder)
   props.actions.mainAction(ACTIONS.CREATE_NEW_POST,catalogueOrder)
   props.history.push('/dashboard/news/edit/'+props.lastItem)
  };

  const getLastItem = () => {
    return
  }
  return (
    <> 
      <Container  className="dashboard">
        <Row>

          <Col lg={{ span: 2 }}>
              <SideNav />
          </Col>
          <Col lg={{span:"10" }}> 
            <section id="product">
              <Form noValidate validated={validated} onSubmit={handleSubmit}>
              <Form.Row>
                    <Col lg="12">
                    <Form.Group as={Col}  controlId="title">
                    <Form.Label>Title</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      placeholder=""
                      defaultValue={title}
                      onChange={e => setTitle(e.target.value)}

                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide a title.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group>
                    </Col>
                </Form.Row>
                <Form.Row>
                    <Col lg="6">
                    <Form.Group as={Col}  controlId="catalogueNumber">
                    <Form.Label>Catalogue #</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      placeholder=""
                      defaultValue={catalogueNumber}
                      onChange={e => setCatalogueNumber(e.target.value)}

                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide a title.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group>
                    </Col>
                    <Col lg="6">
                    <Form.Group as={Col}  controlId="catalogueNumber">
                    <Form.Label>Composer</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      placeholder=""
                      defaultValue={catalogueNumber}
                      onChange={e => setComposerArranger(e.target.value)}

                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide a title.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group>
                    </Col>
                </Form.Row>
                <Form.Row>
                    <Col lg="6">
                    <Form.Group as={Col}  controlId="duration">
                    <Form.Label>Duration</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      placeholder=""
                      defaultValue={duration}
                      onChange={e => setDuration(e.target.value)}

                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide a title.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group>
                    </Col>
                    <Col lg="6">
                    <Form.Group as={Col}  controlId="instrumentation">
                    <Form.Label>Instrumentation</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      placeholder=""
                      defaultValue={instrumentation}
                      onChange={e => setInstrumentation(e.target.value)}

                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide a title.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group>
                    </Col>
                </Form.Row>
                <Form.Row>
                    <Col lg="6">
                    <Form.Group as={Col}  controlId="priceInDollars">
                    <Form.Label>Price In Dollars</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      placeholder=""
                      defaultValue={priceInDollars}
                      onChange={e => setPriceInDollars(e.target.value)}

                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide a title.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group>
                    </Col>
                    <Col lg="6">
                    <Form.Group as={Col}  controlId="priceInEuros">
                    <Form.Label>Price In Euros</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      placeholder=""
                      defaultValue={priceInEuros}
                      onChange={e => setPriceInEuros(e.target.value)}
                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide a title.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group>
                    </Col>
                </Form.Row>
                <Button type="submit">Create Order</Button>
              </Form> 
            </section>
          </Col>
        </Row> 
      </Container>
    </>
  );
};
function mapStateToProps(state) {
    let lastItem = _.orderBy(state.postsReducer,'ID','desc').map((lastItem,i) =>{
        return i===0 ? lastItem.ID :""
    })
  return {
    post: state.singlePostReducer,
    posts:state.postsReducer,
    postImages:state.postImagesReducer,
    lastItem:parseInt(lastItem[0])+1
  };
}

function mapDispatchToProps(dispatch) {
  return {
    actions: bindActionCreators({mainAction}, dispatch)
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(NewMusicCatalogue);
