import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { mainAction } from "redux/actions/index.actions"
import { Table, Form, Row, Col, Button} from "react-bootstrap";
import { ACTIONS } from "redux/actions/types";
import _ from "lodash"
const AdditionalInfo = (props) => {
    useEffect(() => {
        // Update the document title using the browser API
        window.scrollTo(0,0)
        props.actions.mainAction(ACTIONS.LOAD_CD_INFO,props.cdId)
        // eslint-disable-next-line react-hooks/exhaustive-deps
      }, []);

      const [status,setStatus] =  useState("")
      const [title,setTitle] = useState("")
      const [time,setTime] = useState("")
      const [grad,setGrade] = useState("")
      const [composer,setComposer] = useState("")
      const [addInfo,setAddInfo] = useState("")
      const [validated, setValidated] = useState(false);
      const handleSubmit = () => {
          return
      }
      const List = () => {
          const displayButtons = (id,whichButtonsToShow) => {
              if(whichButtonsToShow)
              return(<><Button  variant="danger">DEL</Button> <Button eventKey={id} onClick={(e)=>{setStatus(e.target.attributes.getNamedItem('eventKey').value)}} variant="primary">UPDATE</Button></>)
              return(<><Button  variant="danger">DEL</Button> <Button eventKey={id} onClick={(e)=>{setStatus(e.target.attributes.getNamedItem('eventKey').value)}} variant="primary">EDIT</Button></>)
          }
          const ListItems = () =>{
              return Object.values(props.additionalInfo).map(info => {
              if(info.id !== status)
              return (<tr>
              <td>{info.title[0]}</td>
              <td>{info.time}</td>
              <td>{info.grade}</td>
              <td>{info.composer}</td>
              <td>{info.addInfo}</td>
              <td>{displayButtons(info.id)}</td>
              </tr>)

return (<tr className={info.id === status ? "active":""} key={info.id} >
<td> <Form.Control
                      required
                      as="select"
                      onChange={e => setTitle(e.target.value)}
                      onBlur={e => setTitle(e.target.value)}
                      defaultValue={info.track_title}
                    >
              <option value="" >SELECT A CD</option>
              {Object.values(_.orderBy(props.allPresentations ? props.allPresentations:[],"cdName","asc")).map
              (CD=>{
              return +CD.id === +info.track_title ? <option key={CD.id} value={CD.id} selected>{CD.cdName}</option>
              : <option key={CD.id} value={CD.id} >{CD.cdName}</option>
              })}

                    </Form.Control></td>
<td><Form.Control
                      required
                      type="text"
                      placeholder=""
                      defaultValue={info.time}
                      onChange={e => setTime(e.target.value)}
                      onBlur={e => setTime(e.target.value)}
                    /></td>
<td><Form.Control
                      required
                      type="text"
                      placeholder=""
                      defaultValue={info.grade}
                      onChange={e => setGrade(e.target.value)}
                      onBlur={e => setGrade(e.target.value)}
                    /></td>
<td><Form.Control
                      required
                      type="text"
                      placeholder=""
                      defaultValue={info.composer}
                      onChange={e => setComposer(e.target.value)}
                      onBlur={e => setComposer(e.target.value)}
                    /></td>
<td><Form.Control
                      required
                      type="textarea"
                      rows="2"
                      placeholder=""
                      defaultValue={info.addInfo}
                      onChange={e => setAddInfo(e.target.value)}
                      onBlur={e => setAddInfo(e.target.value)}
                    /></td>
<td><Button  variant="danger">DEL</Button> <Button eventKey={info.id} onClick={(e)=>{setStatus(e.target.attributes.getNamedItem('eventKey').value)}} variant="primary">EDIT</Button></td>
</tr>)
              })

          }
          return (
            <Table striped bordered hover className="additional-info">

            <thead>
              <tr>
                <th>title</th>
                <th>time</th>
                <th>grade</th>
                <th>composer</th>
                <th>additional info</th>
                <th><Button  variant="primary">ADD INFO</Button></th>
              </tr>
            </thead>
            <tbody>
               <ListItems />
            </tbody>

          </Table>
          )
      }
      console.log(props)
      return (<>
        <List />
      </>)
}

function mapStateToProps(state) {

    return {
    allPresentations:_.orderBy(state.AllPresentationsReducer,"id","asc"),
      additionalInfo:state.cdInfoReducer
    };
  }

  function mapDispatchToProps(dispatch) {
    return {
      actions: bindActionCreators({mainAction}, dispatch)
    };
  }

  export default connect(mapStateToProps, mapDispatchToProps)(AdditionalInfo);
