import React from "react";
import Pagination from 'react-bootstrap/Pagination'
import {Row,Col} from "react-bootstrap"
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { mainAction } from 'redux/actions/index.actions'
import { ACTIONS } from "redux/actions/types"

const Paginate = (props) => {
    const loadAgendas = (skip) => {
        window.scrollTo(0,0)
        props.actions.mainAction(ACTIONS.LOAD_AGENDAS,{limit:props.limit,skip}) 
    }

    const renderPageItems = () => {
        let totalPages = Object.keys(props.items).length

        let pages = parseInt(totalPages / props.limit);
        console.log({total:totalPages,pages,limit:props.limit})

        const Items = []
        for (let i = 0; i < pages; i++) {

            let pagesToSkip = i === 1 ? 0: parseInt(i * props.limit)
            Items.push(<Pagination.Item  onClick={()=>loadAgendas(pagesToSkip)}>{i+1}</Pagination.Item> )
        }
        return Items
    }
    console.log(props)
    return(<Row>
        <Col>
        <Pagination style={{margin:"0 auto"}}>
        {renderPageItems()}
        {/* {<Pagination.Item disabled>{14}</Pagination.Item>} */}
        {/* {<Pagination.Ellipsis />} */}
        </Pagination>
        </Col>
        </Row>)
}
function mapStateToProps(state) {
    return {
        agendas:state.agendaReducer
    };
  }

  function mapDispatchToProps(dispatch) {
    return {
      actions: bindActionCreators(
        {
            mainAction
        },
        dispatch
      )
    };
  }

  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(Paginate);
