        backgroundPosition:"10% 50%",
        overflow:"hidden"
    }

    const LeadImage = (props) => {
        let imageName = props.post[0]
        console.log(imageName)
         return  <img src={`/images/posts/${Object.keys(props.post).length > 0  ? "test":""}`} style={{width:"100%",backgrounSize:"cover"}} />
    }

    return (<>
        <Container>
            <section className="article">
            <Row>
                <Col style={styles} className="lead-image" >
                   <LeadImage  {...props}/>
                </Col>
            </Row>
            <Row>
                <Col>
    <h1>{postTitle}</h1>
    <div className="conten">{Object.keys(props.post).length > 0 ? ReactHtmlParser(props.post[0].postContent):"Title"}</div>
                </Col>
            </Row>
            </section>
        </Container>
    </>
    )
}
function mapStateToProps(state,props) {
    let allposts = state.postsReducer
    return {
      post:Object.values(allposts).filter(post => post._id === props.match.params.id),
      posts:state.postsReducer
    };
  }

  function mapDispatchToProps(dispatch) {
    return {
      actions: bindActionCreators({mainAction}, dispatch)
    };
  }

  export default connect(mapStateToProps, mapDispatchToProps)(Post);
