import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { Form, Container, Row, Col, Button } from "react-bootstrap";
import { ACTIONS } from "redux/actions/types";
import Navbar from "components/dashboard/Navbar";
import SideNav from "components/dashboard/SideNav";
import { mainAction } from "redux/actions/index.actions"
const EditPost = props => {
  const {postTitle,postDate,postContent} = props.post
  const [showPost, setShowPost] = useState(false);
  const [title, setTitle] = useState(postTitle);
  const [date, setDate] = useState();
  const [content, setContent] = useState();
  const [postImage, setPostImage] = useState();
  const [postImageCaption, setPostImageCaption] = useState();
  useEffect(() => {
    // Update the document title using the browser API
    document.title = `Johan De Meij | Edit Post`;

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <>
      <Container id="dashboard">
        <Row>
          <Col lg={{ span: 2 }}>
            <SideNav />
          </Col>
          <Col lg={{ span: 10 }}>
            <Container>
              <h2>Edit Posts- {postTitle}</h2>
              <Col lg={{ span: "6" }}>
                <Form.Row>
                  <Form.Group controlId="showpost">
                    <Form.Label>Show Post</Form.Label>
                    <Form.Control
                      as="select"
                      custom
                      onChange={e => setShowPost(e.target.value)}
                    >
                      <option value="t">ON</option>
                      <option>Off</option>
                    </Form.Control>
                  </Form.Group>
                </Form.Row>
                <Form.Row>
                  <Form.Group as={Col} controlId="title">
                    <Form.Label>Title</Form.Label>
                    <Form.Control
                    required
                      as={"text"}
                      placeholder={title}
                      defaultValue={title}
                      onChange={e => setTitle(e.target.value)}
                    />
                  </Form.Group>
                </Form.Row>
                <Form.Row>
                  <Form.Group as={Col} controlId="date">
                    <Form.Label>Date</Form.Label>
                    <Form.Control
                      as={"text"}
                      placeholder=""
                      defaultValue=""
                      onChange={e => setTitle(e.target.value)}
                    />
                  </Form.Group>
                </Form.Row>
                <Form.Row>
                  <Form.Group as={Col} controlId="content">
                    <Form.Label>Content</Form.Label>
                    <Form.Control
                      as="textarea"
                      placeholder=""
                      defaultValue=""
                      onChange={e => setTitle(e.target.value)}
                    />
                  </Form.Group>
                </Form.Row>
                <Form.Row>
                  <Button variant="primary" type="submit">
                    Update
                  </Button>
                </Form.Row>
              </Col>
            </Container>
          </Col>
        </Row>
      </Container>
    </>
  );
};
function mapStateToProps(state) {
  return {
    post: state.singlePostReducer
  };
}

function mapDispatchToProps(dispatch) {
  return {
    actions: bindActionCreators({mainAction}, dispatch)
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(EditPost);
