import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { mainAction } from "redux/actions/index.actions";
import { Form, Row, Col, Button, Container } from "react-bootstrap";
import { ACTIONS } from "redux/actions/types";
import SideNav from "components/dashboard/SideNav";
import dayjs from "dayjs";
const GalleryImage = (props) => {
  let mainProps = props;
  let d = new Date();
  const [id, setId] = useState(dayjs(d).format("YYYYMMDDHHmmss"));
  const [selectedFile, setSelectedFile] = useState();
  const [uploadStatus, setUploadStatus] = useState(false);
  const [validated, setValidated] = useState(false);
  const [file, setFile] = useState();

  useEffect(() => {
    document.title = `Johan De Meij | Edit Post`;

    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  const handleImageSubmit = (e) => {
    // console.log(props)
  };

  const uploadImage = () => {
    let image = { name: file, id, date: d, path: selectedFile};
    mainProps.actions.mainAction(ACTIONS.INSERT_GALLERY_IMAGE, image);
  };

  const deleteGalleryImage = () => {
    let image = { docId: props.docId };
    mainProps.actions.mainAction(ACTIONS.DELETE_GALLERY_IMAGE, image);
  };
  const onChangeHandler = (e) => {
    setSelectedFile(window.URL.createObjectURL(e.target.files[0]));
    setFile(e.target.files[0]);
    setUploadStatus(true);
  };

  const selecteImageToUpload = (e) => {
    document.querySelector("input#gallery-image").click();
  };

  const renderImage = () => {
    if (props.image) {
      return (
        <img
          width="100%"
          height="100%"
          object-fit="contain"
          src={
            !selectedFile
              ? "https://firebasestorage.googleapis.com/v0/b/johandemeij-513b2.appspot.com/o/gallery%2F" +
                props.image +
                "?alt=media"
              : selectedFile
          }
          onClick={(e) => selecteImageToUpload(e)}
        />
      );
    } else {
      return (
        <img
          width="100%"
          height="100%"
          object-fit="contain"
          src={!selectedFile ? "/images/missing.png" : selectedFile}
          onClick={(e) => selecteImageToUpload(e)}
        />
      );
    }
  };

  return (
    <>
      <Container className="dashboard">
        <Row>
          <Col lg={{ span: 2 }}>
            <SideNav />
          </Col>
          <Col lg={{ span: "10" }}>
            <section id="images">
              <Form
                noValidate
                validated={validated}
              >
                <Row>
                  <Col lg={{ span: 4 }} className="gallery-image">
                    {renderImage()}
                    <div
                      style={{
                        marginTop: "15px",
                        textAlign: "center",
                        font: "400 8px/10px 'Work Sans', sans-serif",
                      }}
                    >
                      {!uploadStatus ? props.imageName : ""}
                    </div>
                  </Col>
                  <Col lg={{ span: 6 }} className="caption">
                    <Form.Row>
                      <Form.Group controlId={"backCaption"}>
                        <Form.Label>Caption</Form.Label>
                        <Form.Control
                          required
                          type="text"
                          placeholder=""
                          defaultValue={props.caption}
                          //onChange={}
                        />
                      </Form.Group>
                    </Form.Row>
                    <Form.Row>
                      <input
                        type="file"
                        id={"gallery-image"}
                        onChange={(e) => onChangeHandler(e)}
                      />
                      <Button
                        variant="dark"
                        onClick={(e) => selecteImageToUpload(e)}
                      >
                        Upload
                      </Button>{" "}

                    </Form.Row>
                  </Col>
                </Row>
              </Form>
              <Row>
              <Col lg={{ span: 2 }} className="gallery-image">
                    {renderImage()}
                  </Col>
              </Row>
            </section>
          </Col>
        </Row>
      </Container>
    </>
  );
};
function mapStateToProps(state) {
  return {
    postImage: state.singlePostReducer,
  };
}

function mapDispatchToProps(dispatch) {
  return {
    actions: bindActionCreators({ mainAction }, dispatch),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(GalleryImage);
