import React,{useEffect,useState} from 'react'
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { mainAction } from 'redux/actions/index.actions'
import { Button } from "react-bootstrap"
import { ACTIONS } from "redux/actions/types"
import EditPost from "components/dashboard/EditPost"
import { Container,Row,Col } from "react-bootstrap"
import SideNav from "components/dashboard/SideNav"
import Loading from "components/shared/Loading"
const MusicCatalogue = (props) => {
    const [section,setSection] = useState("list")
    useEffect(() => {
        // Update the document title using the browser API
        document.title = `JDM Dashboard | Presentations`;
        //if(props.categories.length === 0)
       props.actions.mainAction(ACTIONS.LOAD_MUSIC_CATALOGUE,[])
       // eslint-disable-next-line react-hooks/exhaustive-deps
    },[])

    const handleClick = () => {
        props.history.push('/dashboard/catalogue/new')
    }
    const loadCatalogue = (id) => {
      props.actions.mainAction(ACTIONS.UPDATE_CATALOGUE,id)
      props.history.push('/dashboard/catalogue/'+id)
    }
    const deleteCatalogue = (id) => {
      props.actions.mainAction(ACTIONS.DELETE_CATALOGUE,id)
      props.history.push('/dashboard/catalogue')
    }
    const PresentationList = () => {
      if(props.catalogue.length > 0)
        return Object.values(props.catalogue).map(catalogueItem =>{
          return ( <tr key={catalogueItem.id}>
            <td className="post-date" >{catalogueItem.catalogueNumber}</td>
            <td className="post-title">{catalogueItem.title}<br />{catalogueItem.instrumentation}</td>
            <td className="composer">{catalogueItem.composerArranger}</td>
            <td className="post-date">{catalogueItem.priceInEuros}</td>
            <td className="post-date">{`$${catalogueItem.priceInDollars}`}</td>

            <td className="post-actions">

              <Button  variant="secondary" onClick={()=>loadCatalogue(catalogueItem.id)} className="btn btn-datatable btn-icon btn-transparent-dark mr-2"><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-more-vertical"><circle cx="12" cy="12" r="1"></circle><circle cx="12" cy="5" r="1"></circle><circle cx="12" cy="19" r="1"></circle></svg></Button> 

            <Button variant="danger" onClick={()=>{if(window.confirm("Delete this item?"))deleteCatalogue(catalogueItem.id)}} className="btn btn-datatable btn-icon btn-transparent-dark"><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-trash-2"><polyline points="3 6 5 6 21 6"></polyline><path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2"></path><line x1="10" y1="11" x2="10" y2="17"></line><line x1="14" y1="11" x2="14" y2="17"></line></svg></Button></td>
          </tr> )
        })
        return <tr><td colSpan={6}><Loading /></td></tr>
    }
    if(section === "list") {
        return (<>
        <Container   className="dashboard">
        <Row>
            <Col lg={{span:2}}>
               <SideNav />
            </Col>
            <Col lg={{span:10}}>
            <table className="table table-hover">
          <thead >
            <tr>
              <th scope="col" style={{"textAlign":"center"}}>Catalogue No.</th>
              <th scope="col" style={{"textAlign":"center"}}>Title</th>
              <th scope="col" style={{"textAlign":"center"}}>Composer / Arranger</th>
              <th scope="col" style={{"textAlign":"center"}}>Price in Euro</th>
              <th scope="col" style={{"textAlign":"center"}}>Price in Dollar</th>
              <th scope="col" style={{"textAlign":"center"}}><Button onClick={()=>handleClick()}>New Catalogue </Button></th>
            </tr>
          </thead>
          <tbody>
           <PresentationList />
          </tbody>
        </table>
            </Col>
        </Row>
    </Container>

            </>)
    } else if (section==="edit") {
        return (<EditPost />)
    } else {
        return null
    }

}

function mapStateToProps(state) {
    return {
      catalogue:state.catalogueReducer
    };
  }

  function mapDispatchToProps(dispatch) {
    return {
      actions: bindActionCreators(
        {
            mainAction
        },
        dispatch
      )
    };
  }

  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(MusicCatalogue);
