import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { Form, Container, Row, Col, Button} from "react-bootstrap";
import { ACTIONS } from "redux/actions/types";
import SideNav from "components/dashboard/SideNav";
import { mainAction } from "redux/actions/index.actions"
import CKEditor from '@ckeditor/ckeditor5-react';
import ClassicEditor from '@ckeditor/ckeditor5-build-classic';
import _ from "lodash"
import PublicationImage from "components/dashboard/PublicationImage"
const NewPublication = props => {
  const [postStatus, setPostStatus] = useState(2);
  const [title, setTitle] = useState();
  const [date, setDate] = useState();
  const [content, setContent] = useState();
  const [validated, setValidated] = useState(false);
  const [postImg,setPostImg] = useState()
  const [lastTotal,setLastTotal] = useState(props.lastItem);


  useEffect(() => {
    // Update the document title using the browser API
    window.scrollTo(0,0)
    props.actions.mainAction(ACTIONS.LOAD_ALL_PRESENTATIONS,[])
   // props.actions.mainAction(ACTIONS.LOAD_DASHBOARD_POSTS,{})
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);
  const handleSubmit = event => {
    event.preventDefault();

    let newsItem = {
        id:"",
        cdName:"",
        subTitle:"",
        composer:"",
        instrumentation:"",
        synopsis:"",
        totalTime:"",
        frontCover:"",
        backCover:"",
        dateCreated:"",
        frontCaption:"",
        backCaption:"",
        category:"",
        codes:"",
        duration:"",
        grade:"",
        cd:"",
        otherCd:"",
        score:"",
        audio:"",
        video:""

    }
    const form = event.currentTarget;
    if (form.checkValidity() === false) {
      event.stopPropagation();
    }
    setValidated(true);
   props.actions.mainAction(ACTIONS.CREATE_NEW_POST,newsItem)
   props.history.push('/dashboard/news/edit/'+props.lastItem)
  };


  return (
    <>
      <Container>
        <Row>

          <Col lg={{ span: 2 }}>
              <SideNav />
          </Col>
          <Col lg={{span:"10" }}> 
          <PublicationImage />
          <PublicationImage />
            <section id="product">
              <Form noValidate validated={validated} onSubmit={handleSubmit}>
              <Form.Row><Col lg="6">
                  <Form.Group as={Col}  controlId="title">

                          <Form.Label>Name</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      placeholder=""
                      //defaultValue={postTitle}
                      onChange={e => setTitle(e.target.value)}

                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide a name.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>


                  </Form.Group> </Col>
                 <Col lg="6">
                  <Form.Group as={Col}   controlId="subtitle">
                  <Form.Label>Sub Title</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      placeholder=""
                      //defaultValue={postTitle}
                      onChange={e => setTitle(e.target.value)}

                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide a Sub title.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>


                  </Form.Group></Col>
                  </Form.Row> <Form.Row>
                  <Col lg="6"><Form.Group as={Col}  controlId="composer">
                    <Form.Label>Composer</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      placeholder=""
                      //defaultValue={postTitle}
                      onChange={e => setTitle(e.target.value)}

                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide a composer.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group></Col>
                  <Col lg="6"> <Form.Group as={Col}  controlId="instrumentation">
                    <Form.Label>Instrumentaion</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      placeholder=""
                      //defaultValue={postTitle}
                      onChange={e => setTitle(e.target.value)}

                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide a instrumentaion.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group></Col>
                  </Form.Row>
                <Form.Row>
                <Col lg="6"><Form.Group as={Col} controlId="totalTime">
                    <Form.Label>Time</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      placeholder=""
                      //defaultValue={postDate}
                      onChange={e => setDate(e.target.value)}

                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide a product description.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group></Col> 
                <Col lg="6"><Form.Group as={Col} controlId="codes">
                    <Form.Label>codes</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      placeholder=""
                      //defaultValue={postDate}
                      onChange={e => setDate(e.target.value)}

                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide a product description.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group></Col>

                </Form.Row>
                <Form.Row>
                <Col lg="6"><Form.Group as={Col} controlId="duration">
                    <Form.Label>Duration</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      placeholder=""
                      //defaultValue={postDate}
                      onChange={e => setDate(e.target.value)}

                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide a duration.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group></Col>
                <Col lg="6"><Form.Group as={Col} controlId="grade">
                    <Form.Label>Grade</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      placeholder=""
                      //defaultValue={postDate}
                      onChange={e => setDate(e.target.value)}

                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide a grade.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group></Col>
                </Form.Row>
                <Form.Row>
                <Col lg="6"> <Form.Group as={Col} controlId="cd">
                    <Form.Label>CD</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      placeholder=""
                      //defaultValue={postDate}
                      onChange={e => setDate(e.target.value)}

                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide a CD.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group></Col>
                <Col lg="6"><Form.Group as={Col} controlId="otherCD">
                    <Form.Label>otherCD</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      placeholder=""
                      //defaultValue={postDate}
                      onChange={e => setDate(e.target.value)}

                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide a cd.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group></Col>

                </Form.Row>
                <Form.Row>
                <Col lg="6"><Form.Group as={Col} controlId="score">
                    <Form.Label>Score</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      placeholder=""
                      //defaultValue={postDate}
                      onChange={e => setDate(e.target.value)}

                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide a score.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group></Col>
                <Col lg="6"><Form.Group as={Col} controlId="audio">
                    <Form.Label>Audio</Form.Label>
                    <Form.Control
                      required
                      type="text"
                      placeholder=""
                      //defaultValue={postDate}
                      onChange={e => setDate(e.target.value)}

                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide audo.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group></Col>




                </Form.Row>
                <Form.Row>
                  <Form.Group as={Col} controlId="video">
                    <Form.Label>Video</Form.Label>
                    <Form.Control
                      required
                      as="textarea" rows="3"
                      //defaultValue={postDate}
                      onChange={e => setDate(e.target.value)}

                    />
                    <Form.Control.Feedback type="invalid">
                        Please provide a video.
                      </Form.Control.Feedback>
                    <Form.Control.Feedback>Looks good!</Form.Control.Feedback>
                  </Form.Group>

                </Form.Row>
                <Form.Row>
                  <Form.Group as={Col} controlId="content">
                    <CKEditor
                        editor={ ClassicEditor }
                        data={content}
                        toolbar= {'heading', '|', 'bold', 'italic', 'link', 'bulletedList', 'numberedList', 'blockQuote' }
                        onInit={ editor => {
                            // You can store the "editor" and use when it is needed.
                           // console.log( 'Editor is ready to use!', editor );
                        } }
                        onChange={ ( event, editor ) => {
                            const data = editor.getData();
                           // console.log( { event, editor, data } );
                            setContent(data)
                        } }
                        onBlur={ ( event, editor ) => {
                          const data = editor.getData();
                            console.log( 'Blur.', editor );
                            setContent(data)
                        } }
                        onFocus={ ( event, editor ) => {
                            console.log( 'Focus.', editor );
                        } }
                    />
                  </Form.Group>
                  </Form.Row>
                <Button type="submit">Create Publication</Button>
              </Form> 
            </section>
          </Col>
        </Row> 
      </Container>
    </>
  );
};
function mapStateToProps(state) {
    let lastItem = _.orderBy(state.postsReducer,'ID','desc').map((lastItem,i) =>{
        return i===0 ? lastItem.ID :""
    })
  return {
    post: state.singlePostReducer,
    posts:state.postsReducer,
    postImages:state.postImagesReducer,
    lastItem:parseInt(lastItem[0])+1
  };
}

function mapDispatchToProps(dispatch) {
  return {
    actions: bindActionCreators({mainAction}, dispatch)
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(NewPublication);
