import { ACTIONS } from 'redux/actions/types.js'
import { mainAction } from "redux/actions/index.actions"
import { db } from "../../firebase";
import _ from "lodash"
const initialState = {

}
export default function cdInfoReducer (state = initialState, action) {

    switch (action.type) {

      case ACTIONS.LOAD_CD_INFO: {

        let stateCopy =[]
        //=======================================================
        db.collection("cd_info")
        .where("cdId","==",action.payload)
       .get()
       .then(querySnapshot => {
         const data = querySnapshot.docs.map(doc => doc.data());
         stateCopy = data;
         stateCopy.map((cdinfo,i)=>{
           //=======================================================
           db.collection("presentations")
           .where("id","==",cdinfo.track_title)
           .get()
           .then(querySnapshot => {
             const data = querySnapshot.docs.map(doc => doc.data());
             stateCopy[i].title =data[0].cdName
             stateCopy[i].duration =data[0].duration
             stateCopy[i].codes =data[0].codes
           });
          //========================================================
        })
        action.asyncDispatch(mainAction(ACTIONS.LOAD_CD_INFO_SUCCESS,stateCopy))
       });
        //=======================================================

        return stateCopy
      }
      case ACTIONS.LOAD_CD_INFO_SUCCESS: {

        return action.payload
      }
      case ACTIONS.LOAD_CD_INFO_FAIL: {

        return state
      }

      default: 
        return {
          ...state
        }
    }
  }
