import React from "react"
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { ACTIONS } from "redux/actions/types"
import { mainAction } from "redux/actions/index.actions"
import {Container,Row,Col} from "react-bootstrap"
const Post = (props) => {
    let styles = {
        maxHeight:"500px",
        width:"100%",
        backgroundPosition:"10% 50%",
        overflow:"hidden"
    }
    console.log(props)
    return (<>
        <Container>
            <section className="article">
            <Row>
                <Col style={styles} className="lead-image">
                    <img src={`/images/posts/${props.post.image}`} style={{width:"100%",backgrounSize:"cover"}} />
                </Col>
            </Row>
            <Row>
                <Col>

                </Col>
            </Row>
            </section>
        </Container>
    </>
    )
}
function mapStateToProps(state) {
    let posts= state.postsReducer
    return {
      post:Object.values(posts).filter(post => post._id === props.match.params.id)
    };
  }

  function mapDispatchToProps(dispatch) {
    return {
      actions: bindActionCreators({mainAction}, dispatch)
    };
  }

  export default connect(mapStateToProps, mapDispatchToProps)(Post);
