import React,{useEffect,useState} from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { mainAction } from 'redux/actions/index.actions'
import { ACTIONS } from "redux/actions/types"
import {Row,Col} from "react-bootstrap"
import AgendaCard from "components/shared/AgendaCard"
import Pagination from 'react-bootstrap/Pagination'
import _ from "lodash"
const Agendas = (props) => {
        const { agendas } = props
        console.log(Object.keys(agendas).length > 0 ? agendas.agendas:"nothing")
        return Object.values(agendas).map(agenda =>{
            if(agenda.orchestra && agenda.orchestra.indexOf('test')===-1)
            return Object.keys(agenda).length > 0 ? <AgendaCard key={agenda._id} {...agenda} display={6}/>:"Loading..."
            return null
        })
    }

const AgendaList  = (props) => {
    //const { agendas } = props.agenda
    const [agendItem,setAgendaItem] = useState()
    useEffect(() => {
       props.actions.mainAction(ACTIONS.LOAD_CD_AGENDA,[])
       // eslint-disable-next-line react-hooks/exhaustive-deps
    },[])

    const renderPagination = (props) => {
        let totalNumOfPages = (props.total / 20)
        console.log(props.total)
        return (<>
        <Row>
        <Col lg={{span:6,offset:3}}>
        <Pagination>
  <Pagination.First />
  <Pagination.Prev />

  <Pagination.Item>{1}</Pagination.Item>
  <Pagination.Ellipsis />

  <Pagination.Item>{10}</Pagination.Item>
  <Pagination.Item>{11}</Pagination.Item>
  <Pagination.Item active>{12}</Pagination.Item>
  <Pagination.Item>{13}</Pagination.Item>
  <Pagination.Item disabled>{14}</Pagination.Item>

  <Pagination.Ellipsis />
  <Pagination.Item>{20}</Pagination.Item>
  <Pagination.Next />
  <Pagination.Last />
</Pagination>
        </Col>
        </Row>
        </>)
    }
    return (<>
    <section className="agenda">
    <h3 style={{textAlign:'center',fontSize:"2rem",textTransform: "uppercase",color:"#FFF"}}>Agenda</h3>
    <div className="container">
         <Row>
             <Agendas {...props}/>
        </Row>
        {renderPagination()}
    </div>
    </section>
    </>)
}
function mapStateToProps(state) {
    return {
        agendas:state.agendaReducer
    };
  }

  function mapDispatchToProps(dispatch) {
    return {
      actions: bindActionCreators(
        {
            mainAction
        },
        dispatch
      )
    };
  }

  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(AgendaList);
