import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { mainAction } from "redux/actions/index.actions"
import { Table, Container, Row, Col, Button} from "react-bootstrap";
import { ACTIONS } from "redux/actions/types";

const AdditionalInfo = (props) => {
    useEffect(() => {
        // Update the document title using the browser API
        window.scrollTo(0,0)
        props.actions.mainAction(ACTIONS.LOAD_CD_INFO,props.cdId)
        // eslint-disable-next-line react-hooks/exhaustive-deps
      }, []);
      const List = () => {
          const ListItems = () =>{
              return Object.values(props.additionalInfo).map(info => {
              return (<tr>
              <td>{info.title[0]}</td>
              <td>{info.time}</td>
              <td>{info.grade}</td>
              <td>{info.composer}</td>
              <td>{info.addInfo}</td>
              <td><Button>DEL</Button> <Button>ADD</Button></td>
              </tr>)
              })
          }
          return (
            <Table striped bordered hover>
            <thead>
              <tr>
                <th>title</th>
                <th>time</th>
                <th>grade</th>
                <th>composer</th>
                <th>additional info</th>
                <th></th>
              </tr>
            </thead>
            <tbody>
              <ListItems />
            </tbody>
          </Table>
          )
      }
      console.log(props)
      return (<>
        <List />
      </>)
}

function mapStateToProps(state) {

    return {
      additionalInfo:state.cdInfoReducer
    };
  }

  function mapDispatchToProps(dispatch) {
    return {
      actions: bindActionCreators({mainAction}, dispatch)
    };
  }

  export default connect(mapStateToProps, mapDispatchToProps)(AdditionalInfo);
