import React, { useEffect, useState } from "react";
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { mainAction } from "redux/actions/index.actions"
import { Table, Form, Row, Col, Button} from "react-bootstrap";
import { ACTIONS } from "redux/actions/types";

const AdditionalInfo = (props) => {
    useEffect(() => {
        // Update the document title using the browser API
        window.scrollTo(0,0)
        props.actions.mainAction(ACTIONS.LOAD_CD_INFO,props.cdId)
        // eslint-disable-next-line react-hooks/exhaustive-deps
      }, []);
      const [status,setStatus] =  useState("")
      const [title,setTitle] = useState("")
      const [time,setTime] = useState("")
      const [grad,setGrade] = useState("")
      const [composer,setComposer] = useState("")
      const [addInfo,setAddInfo] = useState("")
      const [validated, setValidated] = useState(false);
      const handleSubmit = () => {
          return
      }
      const List = () => {
          const ListItems = () =>{
              return Object.values(props.additionalInfo).map(info => {
                  console.log(info.id,status)
              if(info.id !== status)
              return (<tr>
              <td>{info.title[0]}</td>
              <td>{info.time}</td>
              <td>{info.grade}</td>
              <td>{info.composer}</td>
              <td>{info.addInfo}</td>
              <td><Button  variant="danger">DEL</Button> <Button eventKey={info.id} onClick={(e)=>{setStatus(e.target.attributes.getNamedItem('eventKey').value)}} variant="primary">EDIT</Button></td>
              </tr>)

return (<tr className={info.id === status ? "active":""} key={info.id} >
<td><Form.Control
                      required
                      type="text"
                      placeholder=""
                      defaultValue={info.title[0]}
                      onChange={e => setTitle(e.target.value)}
                      onBlur={e => setTitle(e.target.value)}
                    /></td>
<td><Form.Control
                      required
                      type="text"
                      placeholder=""
                      defaultValue={info.time}
                      onChange={e => setTitle(e.target.value)}
                      onBlur={e => setTitle(e.target.value)}
                    /></td>
<td><Form.Control
                      required
                      type="text"
                      placeholder=""
                      defaultValue={info.grade}
                      onChange={e => setTitle(e.target.value)}
                      onBlur={e => setTitle(e.target.value)}
                    /></td>
<td><Form.Control
                      required
                      type="text"
                      placeholder=""
                      defaultValue={info.composer}
                      onChange={e => setTitle(e.target.value)}
                      onBlur={e => setTitle(e.target.value)}
                    /></td>
<td><Form.Control
                      required
                      type="text"
                      placeholder=""
                      defaultValue={info.addInfo}
                      onChange={e => setTitle(e.target.value)}
                      onBlur={e => setTitle(e.target.value)}
                    /></td>
<td><Button  variant="danger">DEL</Button> <Button eventKey={info.id} onClick={(e)=>{setStatus(e.target.attributes.getNamedItem('eventKey').value)}} variant="primary">EDIT</Button></td>
</tr>)
              })

          }
          return (
            <Table striped bordered hover className="additional-info">

            <thead>
              <tr>
                <th>title</th>
                <th>time</th>
                <th>grade</th>
                <th>composer</th>
                <th>additional info</th>
                <th><Button  variant="primary">ADD INFO</Button></th>
              </tr>
            </thead>
            <tbody>
               <ListItems />
            </tbody>

          </Table>
          )
      }
      console.log(props)
      return (<>
        <List />
      </>)
}

function mapStateToProps(state) {

    return {
      additionalInfo:state.cdInfoReducer
    };
  }

  function mapDispatchToProps(dispatch) {
    return {
      actions: bindActionCreators({mainAction}, dispatch)
    };
  }

  export default connect(mapStateToProps, mapDispatchToProps)(AdditionalInfo);
