import { ACTIONS } from 'redux/actions/types.js'
import { mainAction } from "redux/actions/index.actions"
import _ from "lodash"
const initialState = {};
export default function agendaReducer (state = initialState, action) {
    switch (action.type) {

      case ACTIONS.LOAD_CD_AGENDA: {
       fetch('http://localhost:3001/api/loadAgendas')
        .then((data) => data.json())
        .then((res) => {
          action.asyncDispatch(mainAction(ACTIONS.LOAD_CD_AGENDA_SUCCESS,res.data))
        }).catch(err => action.asyncDispatch(mainAction(ACTIONS.LOAD_CD_AGENDA_FAIL,err)))

        return state
      }
      case ACTIONS.LOAD_CD_AGENDA_SUCCESS: {
      let stateCopy = []
      action.payload.map(agenda => {
        stateCopy.id = agenda.id
        stateCopy.orchestra= agenda.orchestra
        stateCopy.cd = agenda.cd
        stateCopy.cd1 = agenda.cd1
        stateCopy.cd2= agenda.cd2
        stateCopy.cd3 = agenda.cd3
        stateCopy.date= agenda.date
        stateCopy.time = agenda.time
        stateCopy.location = agenda.location
        stateCopy.city = agenda.city
        stateCopy.country = agenda.country
        stateCopy.synopsis = agenda.synopsis
        stateCopy.day = agenda.day
        stateCopy.month = agenda.month
        stateCopy.year = agenda.year
        stateCopy.title = agenda.title
        stateCopy.ON_OFF = agenda.ON_OFF
        stateCopy.conductor = agenda.conductor
      })
      console.log(stateCopy)
        return _.sortBy(action.payload,'id')
      }
      case ACTIONS.LOAD_CD_AGENDA_FAIL: {

        return state
      }

      default: 
        return {
          ...state
        }
    }
  }
