import React,{useEffect} from 'react'
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { Container,Row,Col } from "react-bootstrap"
import Breadcrum from "components/shared/Breadcrum"
import { mainAction } from "redux/actions/index.actions"
import { ACTIONS } from "redux/actions/types"

const Dashboard = (props) => {
    useEffect(() => {
        // Update the document title using the browser API
        document.title = `theLink | Dashboard`;
        //if(props.categories.length === 0)
        props.actions.mainAction(ACTIONS.LOAD_CATEGORIES,{})
        props.actions.mainAction(ACTIONS.LOAD_COMPANIES,{})
        props.actions.mainAction(ACTIONS.LOAD_PRODUCTS,{})
    },[])

    const Companies = (props) => {
        console.log(props)
        let list = Object.values(props).map((company)=>{
            return (
            <li>{company.businessName}</li>
            )
        })
        return (<>
            <section>
                <h2>Companies</h2>
                <ul>
                {list}
                </ul>
            </section>
        </>)
    }
    const Products = (props) => {
        console.log(props)
        let list = Object.values(props).map((product)=>{
            return (
            <li>{product.title}</li>
            )
        })
        return (<>
            <section>
                <h2>Products</h2>
                <ul>
                {list}
                </ul>
            </section>
        </>)
    }
    const Categories = (props) => {
        let list = Object.values(props).map((category)=>{
            return (
            <li>{category.name}</li>
            )
        })
        return (<>
            <section>
                <h2>Categories</h2>
                <ul>
                {list}
                </ul>
            </section>
        </>)
    }
    return(<>
    <Breadcrum header="Dashboard"/>
    <Container id="dashboard">
        <Row>
            <Col lg={{span:4}}>
                <Products {...props.products} />
                <Companies {...props.companies} />
                <Categories {...props.categories} />
            </Col>
        </Row>
    </Container>
    </>)
}
function mapStateToProps(state) {
    return {
       user:state.userReducer,    
       categories:state.categories,
       companies:state.companies,
       products:state.products
    };
  }

  function mapDispatchToProps(dispatch) {
    return {
      actions: bindActionCreators(
        {
            mainAction
        },
        dispatch
      )
    };
  }

  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(Dashboard);
