import React, { useState, useEffect, useCallback } from "react";
import { connect } from "react-redux";
import Carousel, { Modal, ModalGateway } from "react-images";
import ReactHtmlParser from "react-html-parser";
import { bindActionCreators } from "redux";
import { ACTIONS } from "redux/actions/types";
import { mainAction } from "redux/actions/index.actions";
import dayjs from "dayjs";
import AliceCarousel from 'react-alice-carousel'
import 'react-alice-carousel/lib/alice-carousel.css'
const PhotoGallery = (props) => {
  const [ currentIndex, setCurrentIngdex ] = useState()
  const [ items, setItems ] = useState([1,2,3,4,5])
  const slideTo = (i) => setCurrentIngdex({ currentIndex: i });
  const slideNext = () => setCurrentIngdex({ currentIndex: this.state.currentIndex + 1 });
  const slidePrev = () => setCurrentIngdex({ currentIndex: this.state.currentIndex - 1 });
  const [onSlideChanged,setSlideChanged] = useState() 
  useEffect(() => {
    document.title = "JohanDeMeij.com | Home";
    props.actions.mainAction(ACTIONS.LOAD_GALLERY,[])
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  const renderImages = () => {
   return Object.values(props.photos).map(photo=>{
      console.log(`https://firebasestorage.googleapis.com/v0/b/johandemeij-513b2.appspot.com/o/gallery%2F${photo.name}?alt=media`)
      return <img key={photo.id} src={`https://firebasestorage.googleapis.com/v0/b/johandemeij-513b2.appspot.com/o/gallery%2F${photo.name}?alt=media`} width="100%"  object-fit="cover"  onDragStart={handleOnDragStart} className="gallery-image" />
    })
  }
  const renderThumbs = () =>{
     return( <ul>{items.map((item, i) =>
        <li key={i} onClick={() => slideTo(i)}>Thumb {item}</li>)}
      </ul>)}
  const renderGallery =(data)=> {
    console.log(data)

    return (<AliceCarousel mouseTrackingEnabled
      dotsDisabled={true}
      buttonsDisabled={true}
      slideToIndex={currentIndex}
      onSlideChanged={(e)=>setSlideChanged({ currentIndex: e.item })}
    >
      { items.map((item, i) => <div key={i} className="yours-custom-class"><h2>{ item }</h2></div>) }
    </AliceCarousel>);
  }
  const handleOnDragStart = (e) => e.preventDefault()
  console.log(props.photos)
  return (<>
  <section className="photo-gallery">
    <div className="container">
      <div className="row">
      <div className="col-10 offset-1 text-center">
      { renderThumbs() }
          <button onClick={() => this.slidePrev()}>Prev button</button>
          <button onClick={() => this.slideNext()}>Next button</button>
        <h1>Gallery</h1>
      {renderGallery(props.photos)}
      </div>
      </div>
    </div>
  </section>
  </>)
}

function mapStateToProps(state) {
  return {
    photos:state.galleryReducer.gallery
  };
}

function mapDispatchToProps(dispatch) {
  return {
    actions: bindActionCreators(
      {
        mainAction,
      },
      dispatch
    ),
  };
}

export default connect(mapStateToProps, mapDispatchToProps)(PhotoGallery);
