import { ACTIONS } from 'redux/actions/types.js'
import { mainAction } from "redux/actions/index.actions"
import _ from "lodash"

const initialState = {
    categories:["Compositions","Amstel Classics","Music for the Theatre","Symphony Orchestra","The Music of S&oslash;ren Hyldgaard","Solo Concertos"],
    leftColumn:[],
    rightColumn:[]
};
export default function musicReducer (state = initialState, action) {
    switch (action.type) {

      case ACTIONS.LOAD_MUSIC: {
       fetch('http://localhost:3001/api/loadPresentations/0')
        .then((data) => data.json())
        .then((res) => {
          action.asyncDispatch(mainAction(ACTIONS.LOAD_MUSIC_SUCCESS,{data:res.data,total:res.data.length}))
          console.log(res.data.length)
        }).catch(err => action.asyncDispatch(mainAction(ACTIONS.LOAD_MUSIC_FAIL,err)))

        return state
      }
      case ACTIONS.LOAD_MUSIC_LEFT: {
          let numToSkip = (action.payload.total / 2) - 1
       fetch('http://localhost:3001/api/loadPresentations/' + numToSkip)
        .then((data) => data.json())
        .then((res) => {
          action.asyncDispatch(mainAction(ACTIONS.LOAD_MUSIC_RIGHT,res.data))
        }).catch(err => action.asyncDispatch(mainAction(ACTIONS.LOAD_MUSIC_FAIL,err)))

        return state
      }
      case ACTIONS.LOAD_MUSIC_RIGHT: {
       fetch('http://localhost:3001/api/loadPresentations/' + action.payload.skip)
        .then((data) => data.json())
        .then((res) => {
          action.asyncDispatch(mainAction(ACTIONS.LOAD_MUSIC_SUCCESS,res.data))
        }).catch(err => action.asyncDispatch(mainAction(ACTIONS.LOAD_MUSIC_FAIL,err)))

        return state
      }
      case ACTIONS.LOAD_MUSIC_SUCCESS: {
          let stateCopy = _.cloneDeep(state)
          console.log(stateCopy,action)
          stateCopy.leftColumn = action.payload
          stateCopy.cds = action.payload
        return stateCopy
      }
      case ACTIONS.LOAD_MUSIC_FAIL: {

        return state
      }

      default: 
        return {
          ...state
        }
    }
  }
