import { ACTIONS } from 'redux/actions/types.js'
import { mainAction } from "redux/actions/index.actions"
import _ from "lodash"

const initialState = {
    categories:["Compositions","Amstel Classics","Music for the Theatre","Symphony Orchestra","The Music of S&oslash;ren Hyldgaard","Solo Concertos"],
    allPrenstations:[],
    leftColumn:[],
    rightColumn:[]
};
export default function musicReducer (state = initialState, action) {
    switch (action.type) {

      case ACTIONS.LOAD_MUSIC: {
       fetch('http://localhost:3001/api/loadPresentations/0')
        .then((data) => data.json())
        .then((res) => {
          action.asyncDispatch(mainAction(ACTIONS.LOAD_MUSIC_SUCCESS,res.data))
        }).catch(err => action.asyncDispatch(mainAction(ACTIONS.LOAD_MUSIC_FAIL,err)))

        return state
      }
