    //       action.asyncDispatch(mainAction(ACTIONS.LOAD_MUSIC_RIGHT,res.data))
    //     }).catch(err => action.asyncDispatch(mainAction(ACTIONS.LOAD_MUSIC_FAIL,err)))

        return state
      }
      case ACTIONS.LOAD_MUSIC_RIGHT: {
       fetch('http://localhost:3001/api/loadPresentations/' + action.payload.skip)
        .then((data) => data.json())
        .then((res) => {
          action.asyncDispatch(mainAction(ACTIONS.LOAD_MUSIC_SUCCESS,res.data))
        }).catch(err => action.asyncDispatch(mainAction(ACTIONS.LOAD_MUSIC_FAIL,err)))

        return state
      }
      case ACTIONS.LOAD_MUSIC_SUCCESS: {
          let stateCopy = _.cloneDeep(state)
          stateCopy.allPrenstations = action.payload
          stateCopy.cds = action.payload
          action.asyncDispatch(mainAction(ACTIONS.LOAD_MUSIC_LEFT,stateCopy))
        return stateCopy
      }
      case ACTIONS.LOAD_MUSIC_FAIL: {

        return state
      }

      default: 
        return {
          ...state
        }
    }
  }
