import { ACTIONS } from 'redux/actions/types.js'
import { updatePost } from 'API/indexAPI'
import _ from 'lodash'


const initialState = {};
export default function singlePostReducer (state = initialState, action) {
    switch (action.type) {
      case ACTIONS.LOAD_POST: {
        return action.payload
      }
      case ACTIONS.UPDATE_POST:{
        let stateCopy = _.cloneDeep(state)
        updatePost(stateCopy).then(json=>{
          action.asyncDispatch(mainAction(UPDATE_POST_SUCCESS,json.data.data))
        }).catch(err=>{
          action.asyncDispatch(mainAction(UPDATE_POST_FAIL,err))
        })
        return stateCopy
      }
      case ACTIONS.UPDATE_POST_SUCCESS:{
        return {state,...action.payload}
      }
      case ACTIONS.UPDATE_POST_FAIL:{
        return state
      }
      default: 
        return state

    }
  }
