import React,{useEffect,useState} from 'react'
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { mainAction } from 'redux/actions/index.actions'
import { Button } from "react-bootstrap"
import { ACTIONS } from "redux/actions/types"
import { Container,Row,Col } from "react-bootstrap"
import SideNav from "components/dashboard/SideNav"
import Loading from "components/shared/Loading"
//import { Link } from "react-router-dom";
import EditPost from "components/dashboard/EditPost"

const DashboardEvents = (props) => {
    const [section,setSection] = useState("list")
    useEffect(() => {
        // Update the document title using the browser API
        document.title = `JDM Dashboard | posts`;
        //if(props.categories.length === 0)
       props.actions.mainAction(ACTIONS.LOAD_EVENTS,[])
       // eslint-disable-next-line react-hooks/exhaustive-deps
    },[])
    const deleteEvent = (_id,ID,imageID) => {

     props.actions.mainAction(ACTIONS.DELETE_POST_IMAGE,{image:imageID,post:ID})
     props.actions.mainAction(ACTIONS.DELETE_POST,{image:_id,post:ID})
    } 
    const handleClick = (props) => {
        //setSection("edit")
        props.history.push('/dashboard/news/new')
    }
    const loadEvent = (ID) => {
      props.history.push('/dashboard/events/edit/'+ID)
    }
    const PostList = () => {
      //return null
      if(Object.keys(props.events).length > 0){

        return Object.values(props.events).map(post =>{
          return ( <tr key={post._id}>
            <td className="post-title">{post.title}</td>
            <td style={{"textAlign":"center"}}></td>
            <td className="post-actions">

              <Button  variant="secondary" className="btn btn-datatable btn-icon btn-transparent-dark mr-2"  onClick={()=>loadEvent(post.id)}><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-more-vertical"><circle cx="12" cy="12" r="1"></circle><circle cx="12" cy="5" r="1"></circle><circle cx="12" cy="19" r="1"></circle></svg></Button> 

            <Button variant="danger" className="btn btn-datatable btn-icon btn-transparent-dark" onClick={()=>deleteEvent(post.id)}><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="feather feather-trash-2"><polyline points="3 6 5 6 21 6"></polyline><path d="M19 6v14a2 2 0 0 1-2 2H7a2 2 0 0 1-2-2V6m3 0V4a2 2 0 0 1 2-2h4a2 2 0 0 1 2 2v2"></path><line x1="10" y1="11" x2="10" y2="17"></line><line x1="14" y1="11" x2="14" y2="17"></line></svg></Button></td>
          </tr> )
        })
      } else {
        return (<tr>
          <td  colSpan="3"><Loading /></td>
        </tr>)
      }
    }

    if(section === "list") {
        return (<>
        <Container   className="dashboard">
        <Row>
            <Col lg={{span:2}}>
               <SideNav />
            </Col>
            <Col lg={{span:10}}>
            <table className="table table-hover">
          <thead >
            <tr>
              <th scope="col" style={{"textAlign":"center"}}>Events</th>
              <th scope="col" style={{"textAlign":"center"}}></th>
              <th scope="col" style={{"textAlign":"center"}}><Button onClick={()=>handleClick(props)}>New Events</Button></th>
            </tr>
          </thead>
          <tbody>
           <PostList />
          </tbody>
        </table>
            </Col>
        </Row>
    </Container>
            </>)
    } else if (section==="edit") {
        return (<EditPost />)
    } else {
        return null
    }

}

function mapStateToProps(state) {
    return {
      events:state.eventsReducer
    };
  }

  function mapDispatchToProps(dispatch) {
    return {
      actions: bindActionCreators(
        {
            mainAction
        },
        dispatch
      )
    };
  }

  export default connect(
    mapStateToProps,
    mapDispatchToProps
  )(DashboardEvents);
