import { ACTIONS } from 'redux/actions/types.js'
import _ from "axios"

const initialState = {};
export default function singlePostReducer (state = initialState, action) {
    switch (action.type) {
      case ACTIONS.LOAD_POST: {
        let stateCopy = _.cloneDeep(state)
        let postContent = action.payload.postContent.replace(/(\r\n|\n|\r)/gm, "")
        stateCopy.postContent = postContent
        return stateCopy
      }
      case ACTIONS.UPDATE_POST:{
        let stateCopy = _.cloneDeep(action.payload)
        let postContent = action.payload.postContent.replace(/(\r\n|\n|\r)/gm, "")
        stateCopy.postContent = postContent
        return stateCopy
      }
      case ACTIONS.UPDATE_POST_SUCCESS:{
        return {state,...action.payload}
      }
      case ACTIONS.UPDATE_POST_FAIL:{
        return state
      }
      default: 
        return state

    }
  }
