import { ACTIONS } from 'redux/actions/types.js'
import { mainAction } from "redux/actions/index.actions"

const initialState = {

};
export default function contactUsReducer (state = initialState, action) {
    switch (action.type) {

      case ACTIONS.SUBMIT_CONTACT_INFO: {
       let stateCopy = _.cloneDeep(state)
       let header = stateCopy.headers.filter(header => header.name === action.payload.navLocation)
       action.asyncDispatch(mainAction(ACTIONS.LOAD_HEADER_SUCCESS,...header))
        return action.navLocation ? action.navLocation: state
      }
      case  ACTIONS.SUBMIT_CONTACT_INFO_SUCCESS: {
        return action.payload
      }
      case  ACTIONS.SUBMIT_CONTACT_INFO_FAIL: {

        return state
      }

      default: 
        return {
          ...state
        }
    }
  }
