import { ACTIONS } from 'redux/actions/types.js'
import { getPost } from "API/indexAPI"
import { mainAction } from "redux/actions/index.actions"

const initialState = {};
export default function singlePostReducer (state = initialState, action) {
    switch (action.type) {

      case ACTIONS.GET_POST:{
        getPost({_id:action.payload})
        .then(json=>{
            action.asyncDispatch(mainAction(ACTIONS.GET_POST_SUCCESS,json.data.data))
        })
        .catch(err=>{
            action.asyncDispatch(mainAction(ACTIONS.GET_POST_FAIL,err))
        })
      }
      case ACTIONS.GET_POST_SUCCESS:{
        return state
      }
      case ACTIONS.GET_POST_FAIL:{
        return state
      }
      default: 
        return state

    }
  }
