import React, { useState, useCallback  } from 'react'
import { connect } from 'react-redux'
import Carousel, { Modal, ModalGateway } from "react-images";
import ReactHtmlParser from 'react-html-parser'; 
import Gallery from "react-photo-gallery";
import { bindActionCreators } from "redux";
import { ACTIONS } from "redux/actions/types"
import { mainAction } from "redux/actions/index.actions"
import dayjs from "dayjs"
const PhotoGallery = (props) => {
    const [currentImage, setCurrentImage] = useState(0);
    const [viewerIsOpen, setViewerIsOpen] = useState(false);

    const openLightbox = useCallback((event, { photo, index }) => {
      setCurrentImage(index);
      setViewerIsOpen(true);
    }, []);

    const closeLightbox = () => {
      setCurrentImage(0);
      setViewerIsOpen(false);
    };
    let photos = '',
        title = '',
        date = '',
        location='',
        synopsis = '',
        video = '',
        galleryId = props.match.params.id

        document.title = "cocomatl.org | " + title + " " +dayjs(date).format('DD/MM/YYYY');
    return (
        <div>
             <div className="container">
                 <div className="row">
                 <div className="col-10 offset-1 text-center">
                    <h1>{title}</h1>
                    <h4>{`${location} - ${dayjs(date).format('MMMM DD, YYYY')}`}</h4>
    {synopsis ? <p>{ReactHtmlParser(synopsis)}</p>:""}
                </div> 
                 </div>
                 <div className="row">
                     <div className="col-10 offset-1">
                     <Gallery photos={photos} onClick={openLightbox} />
                     <ModalGateway>
        {viewerIsOpen ? (
          <Modal onClose={closeLightbox}>
            <Carousel
              currentIndex={currentImage}
              views={photos.map(x => ({
                ...x,
                srcset: x.srcSet,
                caption: x.title
              }))}
            />
          </Modal>
        ) : null}
      </ModalGateway>
                     </div>
                 </div>
                 <div className="row">
                   <div className="col" style={{textAlign:"center",marginTop:"25px"}}>
                   {ReactHtmlParser(video)}
                   </div>
                 </div>
             </div>
        </div>
    )
}


function mapStateToProps(state) {
    return {

    };
  }

  function mapDispatchToProps(dispatch) {
    return {
      actions: bindActionCreators(
        {
            mainAction
        },
        dispatch
      )
    };
  }

  export default connect(mapStateToProps,mapDispatchToProps)(PhotoGallery);
